catalog_script_client.script:
function onLoad() {
    var gr = new GlideRecord('sys_choice');
    gr.addQuery('name', 'sc_cat_item');
    gr.addQuery('element', g_form.getUniqueValue());
	gr.addQuery('dependent_value','carrier');
	gr.addQuery('inactive',false);
    gr.query(checkRecordCount);
}
function setVisibility(visibility, mandatory, readOnly){
		g_form.setMandatory('carrier', mandatory);
		g_form.setReadOnly('carrier', readOnly);
        g_form.setDisplay('carrier', visibility);
}

function checkRecordCount(queryGR) {
	var visibility = true;
	var readOnly = false;
	var mandatory = true;

	//If there are no choices, hide color
    if (!queryGR.hasNext()) {
		visibility = false;
		mandatory = false;
		setVisibility(visibility, mandatory, readOnly);
    }
	//If there is only one choice, set the color value to that and make it read only
	else if (queryGR.next() && !queryGR.hasNext()){
		g_form.setValue('carrier',queryGR.sys_id);
		readOnly = true;
		mandatory = false;
		setVisibility(visibility, mandatory, readOnly);
		if(queryGR.value.toString() == "Verizon"){
        setTimeout(function() {
            g_form.showFieldMsg('carrier', "Verizon is APi Group's carrier for all new and upgraded mobile devices", 'INFO', false);
        }, 1000); // 100 ms delay
		}
	}
}
