catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	var companyNumber = '';
	var departmentNumber = '';
	var companyCode = '';
	var gl = '';
	var isMobile = g_form.getBooleanValue('is_mobile');
	var company = g_form.getValue('company');
	if(isMobile){
		var ga = new GlideAjax('global.GetCellularGL'); //Script include
		ga.addParam('sysparm_name','getGl'); //Method/Function
		ga.addParam('company',company); //Parameter
		ga.getXMLAnswer(function(response){
			gl = response;
			if(gl == null){
				gl = "";
			}
			setValues();
		});
	}
	else{
		gl = '70700';
		setValues();
	}
	function setValues(){
		g_form.getReference('company', function(companyGr){
			if(companyGr && companyGr.u_company_number && companyGr.u_company_code){
				companyNumber = companyGr.u_company_number.toString();
				companyCode = companyGr.u_company_code.toString();
				g_form.getReference('location', function(locationGr){
					if(locationGr && locationGr.u_department_number){
						departmentNumber = locationGr.u_department_number.toString();
						g_form.setValue('chargeback_and_configuration', companyNumber + ' - ' + departmentNumber + ' - ' + gl + ' Group Tag: ' + companyCode);
					}
				});
			}
		});	
	}
}
