catalog_script_client.script:
function onLoad() {
    var gr = new GlideRecord('sys_choice');
    gr.addQuery('name', 'sc_cat_item');
    gr.addQuery('element', g_form.getUniqueValue());
	gr.addQuery('dependent_value','color');
	gr.addQuery('inactive',false);
    gr.query(checkRecordCount);
}

function checkRecordCount(queryGR) {
	var visibility = true;
	var readOnly = false;
	var mandatory = true;

	//If there are no choices, hide color
    if (!queryGR.hasNext()) {
		visibility = false;
		mandatory = false;
    }
	//If there is only one choice, set the color value to that and make it read only
	else if (queryGR.next() && !queryGR.hasNext()){
		g_form.setValue('color',queryGR.sys_id);
		readOnly = true;
		mandatory = false;
	}
		g_form.setMandatory('color', mandatory);
		g_form.setReadOnly('color', readOnly);
        g_form.setDisplay('color', visibility);
}
