sys_script_include.script:
var GetCellularGL = Class.create();
GetCellularGL.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	type: 'GetCellularGL',
	getGl: function(company){
		var gl;
		var companyId = company || this.getParameter('company');
		(function() {
			//var gl; was defined here
			var companyGr = new GlideRecord('core_company');
			companyGr.get(companyId);
			try {
				var inputs = {};
				inputs['company'] = companyGr;

				var result = sn_fd.FlowAPI.getRunner().subflow('global.prof__get_cellular_gl__s').inForeground().withInputs(inputs).run();
				var outputs = result.getOutputs();
				gl = outputs['gl'];

			} catch (ex) {
				var message = ex.getMessage();
				gs.error(message);
			}

		})();
		return gl;
	}
});
